Office.context.mailbox.item.recurrenceSetAsync:
var date = new Date();
var seriesTime = new Office.SeriesTime();
seriesTime.setStartDate(date.getFullYear(), date.getMonth(), date.getDate()); 
seriesTime.setEndDate(date.getFullYear() + 1, date.getMonth(), date.getDate());
seriesTime.setStartTime(date.getHours(), date.getMinutes()); 
seriesTime.setDuration(30);
var recurrence = {
	'recurrenceType': 'daily',
	'recurrenceProperties': {
		'interval': 1
	},
	'seriesTime': seriesTime,
	'recurrenceTimeZone': {
		'name': 'Pacific Standard Time', 
		'offset': -480
	}
}
Office.context.mailbox.item.recurrence.setAsync(
	recurrence, 
	function(asyncResult) {
		console.log(JSON.stringify(asyncResult));
	}
);

